{
    "name": "application-msvc-name",
    "isSystem": false,
    "description": "Description",
    "isActivated": true,
    "microservices": [
        {
            "name": "msvc-1",
            "config": "string",
            "images": [
                {
                    "containerImage": "hello-world",
                    "fogTypeId": 1
                },
                {
                    "containerImage": "hello-world",
                    "fogTypeId": 2
                }
            ],
            "registryId": 1,
            "application": "application-msvc-name",
            "iofogUuid": "{{node-id-2}}",
            "rootHostAccess": true,
            "logSize": 0,
            "volumeMappings": [
                {
                    "hostDestination": "/var/dest",
                    "containerDestination": "/var/dest",
                    "accessMode": "rw"
                }
            ],
            "ports": [
                {
                    "internal": 1881,
                    "external": 1882
                }
            ],
            "routes": [],
            "env": [
                { 
                    "key": "selfname",
                    "value": "{{ self.name | upcase }}"
                },
                {
                    "key": "sharedToken",
                    "value": "sekrittoken"
                },
                {
                    "key": "http_proxy",
                    "value": "http://myproxy:8080/"
                },
                {
                    "key": "https_proxy",
                    "value": "{{ self.microservices | where: \"name\", \"msvc-1\" | first | map: \"env\" | first | where: \"key\" , \"http_proxy\" | first | map: \"value\" | first }}"
                }
            ],
            "cmd": [
                "test",
                "--arg1",
                "--arg2"
            ]
        },
        {
            "name": "msvc-2",
            "config": "string",
            "images": [
                {
                    "containerImage": "hello-world",
                    "fogTypeId": 1
                },
                {
                    "containerImage": "hello-world",
                    "fogTypeId": 2
                }
            ],
            "registryId": 1,
            "application": "application-msvc-name",
            "iofogUuid": "{{node-id-2}}",
            "rootHostAccess": true,
            "logSize": 0,
            "volumeMappings": [
                {
                    "hostDestination": "/var/dest",
                    "containerDestination": "/var/dest",
                    "accessMode": "rw"
                }
            ],
            "ports": [],
            "routes": [],
            "env": [
                { 
                    "key": "selfname",
                    "value": "{{ self.name | upcase }}"
                },
                {
                    "key": "sharedToken",
                    "value": "{{ self.microservices | where: \"name\", \"msvc-1\" | first | map: \"env\" | first | where: \"key\", \"sharedToken\" | first | map: \"value\" | first }}"
                },
                {
                    "key": "http_proxy",
                    "value": "{{ self.microservices | where: \"name\", \"msvc-1\" | first | map: \"env\" | first | where: \"key\" , \"http_proxy\" | first | map: \"value\" | first }}"
                },
                {
                    "key": "https_proxy",
                    "value": "{{ self.microservices | where: \"name\", \"msvc-1\" | first | map: \"env\" | first | where: \"key\" , \"http_proxy\" | first | map: \"value\" | first }}"
                },
                {
                    "key": "rulesengineHOST",
                    "value": "{%  assign curmsvc= self.microservices | where: \"name\", \"msvc-1\" | first %}{{ curmsvc | findMicroserviceAgent | map: \"host\" | first }}"
                },
                {
                    "key": "rulesenginePORT",
                    "value": "{{ self.microservices | where: \"name\", \"msvc-1\" | first | map: \"ports\" | first | map: \"external\" | first | toString }}"
                },
                {
                    "key": "redisHost",
                    "value": "{% assign redisApp = \"application-name-1\" | findApplication %}{% assign redismsvc =  redisApp.microservices | where: \"name\", \"redistest\" | first %}{{ redismsvc | findMicroserviceAgent | map: \"host\"}}:{{ redismsvc | map: \"ports\" | first | first | map: \"external\" | first | toString }}"
                },
                {
                    "key": "edgeResLiveness",
                    "value": "{{ \"com.orange.smart-door\" | findEdgeResource: \"0.0.1\" | map: \"interface\" | map: \"endpoints\" | first  | where: \"name\", \"liveness\" | first | map: \"url\" | first }}"
                },
                {
                    "key": "edgeResVersion",
                    "value": "{{ \"com.orange.smart-door\" | findEdgeResource            | map: \"interface\" | map: \"endpoints\" | first  | where: \"name\", \"version\" | first | map: \"url\" | first }}"
                }
            ],
            "cmd": [
                "test",
                "--arg1",
                "--arg2"
            ]
        },
